// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title Router
 * @dev 寄付金の分配を管理するRouterコントラクト
 * 寄付金を基金、Burn、受取人の3つに分配します
 */
contract Router is AccessControl {
    /// @notice 基金管理者ロール
    bytes32 public constant FUND_MANAGER_ROLE = keccak256("FUND_MANAGER_ROLE");

    /// @notice 分配比率管理者ロール
    bytes32 public constant RATIO_MANAGER_ROLE =
        keccak256("RATIO_MANAGER_ROLE");

    /// @notice Burnアドレス（0x0）
    address public constant BURN_ADDRESS = address(0);

    /// @notice 基金への分配比率（基準: 10000 = 100%）
    uint256 public fundRatio;

    /// @notice Burnの分配比率（基準: 10000 = 100%）
    uint256 public burnRatio;

    /// @notice 基金ウォレットアドレス
    address public fundWallet;

    /**
     * @dev コンストラクタ
     * @param _fundWallet 基金ウォレットアドレス
     * @param _fundRatio 基金への分配比率（基準: 10000 = 100%）
     * @param _burnRatio Burnの分配比率（基準: 10000 = 100%）
     */
    constructor(address _fundWallet, uint256 _fundRatio, uint256 _burnRatio) {
        fundWallet = _fundWallet;
        fundRatio = _fundRatio;
        burnRatio = _burnRatio;

        // デフォルト管理者ロールの設定
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(FUND_MANAGER_ROLE, _msgSender());
        _grantRole(RATIO_MANAGER_ROLE, _msgSender());
    }

    /**
     * @dev 基金の分配比率を更新する
     * @param _fundRatio 新しい基金への分配比率（基準: 10000 = 100%）
     */
    function setFundRatio(
        uint256 _fundRatio
    ) external onlyRole(RATIO_MANAGER_ROLE) {
        require(_fundRatio + burnRatio <= 10000, "Total ratio exceeds 100%");
        fundRatio = _fundRatio;
    }

    /**
     * @dev Burnの分配比率を更新する
     * @param _burnRatio 新しいBurnの分配比率（基準: 10000 = 100%）
     */
    function setBurnRatio(
        uint256 _burnRatio
    ) external onlyRole(RATIO_MANAGER_ROLE) {
        require(fundRatio + _burnRatio <= 10000, "Total ratio exceeds 100%");
        burnRatio = _burnRatio;
    }

    /**
     * @dev 基金ウォレットアドレスを更新する
     * @param _fundWallet 新しい基金ウォレットアドレス
     */
    function setFundWallet(
        address _fundWallet
    ) external onlyRole(FUND_MANAGER_ROLE) {
        require(_fundWallet != address(0), "Invalid fund wallet address");
        fundWallet = _fundWallet;
    }
}

project/contracts/RouterFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Create2} from "@openzeppelin/contracts/utils/Create2.sol";
import {Router} from "./Router.sol";

contract RouterFactory {
    function deploy(
        bytes32 _salt,
        address _fundWallet,
        uint256 _fundRatio,
        uint256 _burnRatio
    ) external returns (address addr) {
        bytes memory bytecode = abi.encodePacked(
            type(Router).creationCode,
            abi.encode(_fundWallet, _fundRatio, _burnRatio)
        );
        addr = Create2.deploy(0, _salt, bytecode);
    }

    function computeAddress(
        bytes32 _salt,
        address _fundWallet,
        uint256 _fundRatio,
        uint256 _burnRatio
    ) external view returns (address addr) {
        bytes memory bytecode = abi.encodePacked(
            type(Router).creationCode,
            abi.encode(_fundWallet, _fundRatio, _burnRatio)
        );
        addr = Create2.computeAddress(_salt, keccak256(bytecode));
    }
}
